'use strict';

/* globals __export, __import */

__import(

	'Class1',

	function(Class1) {
		
		var Class2 = function(){};

		Class2.prototype = new Class1();

		Class2.prototype.constructor = Class1;
		Class2.prototype.firstName = 'Class2';
		Class2.prototype.lastName = 'Class2';
		__export('Class2',Class2);

		var c1 = new Class1('Adams');
		var c2 = new Class2('Jacobs');

		c1.sayMyName();
		c2.sayMyName();

		c1 = new Class1();
		c2 = new Class2();
		c1.sayMyName();
		c2.sayMyName();
	
	}

);

(function create_Class1() {
 
	var Class1 = function(lastName){
		this.lastName = lastName;
	};
	Class1.prototype.firstName = 'Class1';
	Class1.prototype.sayMyName = function() {
		console.log('My name is', this.firstName, this.lastName);
	};
	
	__export('Class1',Class1);

})();

